mapper.select:
SELECT COUNT(*)
FROM ICOUN_POSTS
WHERE PST_DEL = '1' AND BBS_SN =
#{bbsSn}

	AND WRITER = #{userId}

SELECT PST_SN, PST_CAT, PST_TTL, WRITER, PSTG_YMD, PST_COMP, BBS_SN
FROM ICOUN_POSTS
WHERE PST_DEL = '1' AND BBS_SN = #{bbsSn}

	AND WRITER = #{userId}

ORDER BY PST_SN DESC
LIMIT #{pageSize} OFFSET #{offset}
