mapper.update:
UPDATE PF_SCHEDULE 
SET PF_SC_DEL = '0' 
WHERE PFS_NO = #{pfs_no};

mapper.insert:
		INSERT INTO PF_COUN_INFO (PF_NO, STUD_NO, PF_COUN_RSVT_YMD, PF_COUN_RSVT_TIME, PFS_NO, PF_CONTENTS) 
		VALUES (#{PF_NO}, #{STUD_NO}, #{PF_COUN_RSVT_YMD}, #{PF_COUN_RSVT_TIME}, #{PFS_NO}, #{PF_CONTENTS});

        	SELECT LAST_INSERT_ID() AS pfCounNo
